import React, { Fragment, useState } from "react";
import TrashClosedIcon from "../../../icons/trash-closed.svg";
import TrashOpenIcon from "../../../icons/trash-open.svg";
import classes from "./deleteButton.module.css";
import { useDispatch, useSelector } from "react-redux";
import { dataActions } from "../../../store/data";


const DeleteButton = () => {
  const isDelete = useSelector((state) => state.data.isDeleteEnabled);
  const dispatch = useDispatch();
  const [isHovered, setIsHovered] = useState(false);

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleLeave = () => {
    setIsHovered(false);
  };



  return (
    <Fragment onMouseEnter={handleHover} onMouseLeave={handleLeave}>
      {!isDelete && (
        <img className={isHovered ? classes.buttonOpen : classes.buttonClosed} 
        src={!isDelete ? TrashOpenIcon : TrashClosedIcon}
        />
      )}
      {isDelete && (
        <img className={isHovered ? classes.buttonClosedDelete : classes.buttonOpenDelete}
        src={isDelete ? TrashClosedIcon : TrashOpenIcon}
        />
      )}
      
    </Fragment>
  );
};

export default DeleteButton;
