import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import ProductViewContainer from "./ProductViewContainer";
import Classes from "./EditDetails.module.css";
import { dataActions } from "../../store/data";

const EditDetails = (props) => {
  const dispatch = useDispatch();
  const dataId = useSelector((state) => state.data.viewDataId);
  const itemData = useSelector((state) =>
    state.data.storeData.find((item) => item.id === dataId)
  );

  const [formData, setFormData] = useState({
    title: itemData.title,
    brand: itemData.brand,
    category: itemData.category,
    price: itemData.price,
    discountPercentage: itemData.discountPercentage,
    description: itemData.description,
    rating: itemData.rating,
    stock: itemData.stock,
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();

    dispatch(dataActions.updateData(formData));
    dispatch(dataActions.deleteData({ itemId: dataId }));

    const newItemData = {
      ...itemData,
      ...formData,
    };
    dispatch(dataActions.addData({ newItem: newItemData }));
    dispatch(dataActions.setIsEdit());
  };

  const handleClose = () => {
    dispatch(dataActions.setIsEdit());
  };

  return (
    <ProductViewContainer>
      <div className={Classes.popupOverlay}>
        <div className={Classes.popupContent}>
          <form onSubmit={handleFormSubmit}>
            <div>
              <label htmlFor="title">Title:</label>
              <input
                type="text"
                id="title"
                name="title"
                maxLength="50"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className={Classes.row}>
              <div className={Classes.leftDiv}>
                <label htmlFor="brand">Brand:</label>
                <input
                  type="text"
                  id="brand"
                  name="brand"
                  maxLength="50"
                  value={formData.brand}
                  onChange={handleInputChange}
                  required
                />
              </div>

              <div className={Classes.rightDiv}>
                <label htmlFor="category">Category:</label>
                <input
                  type="text"
                  id="category"
                  name="category"
                  maxLength="50"
                  value={formData.category}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>

            {/* Rest of the form inputs */}
            
            <div className={Classes.buttons}>
              <button className={Classes.submitButton} type="submit">
                Submit
              </button>
              <button className={Classes.cancelButton} onClick={handleClose}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      </div>
    </ProductViewContainer>
  );
};

export default EditDetails;
