import React, { Fragment, useState } from 'react';
import { useSelector } from 'react-redux';

import Classes from "./ItemList.module.css"

import Items from './Items';

const ItemList = (props) => {
  const storeData = useSelector(state => state.data.storeData)
  const [loadNum, setLoadNum] = useState(9)
  
  if (!storeData || storeData.length === 0) {
    return null;
  }

  const firstNineItems = storeData.slice(0, loadNum);

  function LoadMore() {
    const handleIncreaseLoadNum = () => {
      setLoadNum(loadNum + 9)
    }
    return (
      <button className={Classes.loadMoreButton} onClick={handleIncreaseLoadNum}>Load More</button>
    )
  }

  

  return (
    <Fragment>
      <div>
      {firstNineItems.map((data) => (
        <Items
          image={data.thumbnail}
          key={data.id}
          id={data.id}
          title={data.title}
          price={data.price}
          brand={data.brand}
        />
      ))}
      </div>
      
    </Fragment>
  );
};

export default ItemList;
