import React, { Fragment, useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { dataActions } from '../../store/data';

import Classes from "./ItemList.module.css"

import Items from './Items';

const ItemList = (props) => {
  const storeLoadNum = useSelector(state => state.data.loadNum);
  const storeData = useSelector(state => state.data.storeData);
  const dispatch = useDispatch();
  
  const [loadNum, setLoadNum] = useState(storeLoadNum);

  useEffect(() => {
    setLoadNum(storeLoadNum);
  }, [storeLoadNum]);

  if (!storeData || storeData.length === 0) {
    return null;
  }

  const firstNineItems = storeData.slice(0, loadNum);
  
  const handleIncreaseLoadNum = () => {
    dispatch(dataActions.increse(setLoadNum)); // Dispatch the increse action and pass the setLoadNum function as payload
  }

  return (
    <Fragment>
      <div>
        {firstNineItems.map((data) => (
          <Items
            image={data.thumbnail}
            key={data.id}
            id={data.id}
            title={data.title}
            price={data.price}
            brand={data.brand}
          />
        ))}
      </div>
      <button onClick={handleIncreaseLoadNum}>Increase Load</button>
    </Fragment>
  );
};

export default ItemList;
