import React, { useEffect, useState } from "react";
import Card from "../UI/Card";
import classes from "./Items.module.css";

const Items = (props) => {
  const [titleLength, setTitleLength] = useState(null);

  useEffect(() => {
    if (props.title) {
      setTitleLength(props.title.length);
      console.log(props.title.lenght)
    }
  }, [props.title]);

  

  return (
    <Card>
      <li className={classes.list}>
        <div className={classes.nameBox}>
          <h2 className={classes.title}>{props.title}</h2>
          <p className={classes.brand}>{props.brand}</p>
        </div>
        {titleLength && titleLength > 30 && <span>...</span>}
        <div className={classes.priceBoxBorder}>
          <i className={classes.price}>$</i>
          <h3 className={classes.price}>{props.price}</h3>
        </div>
        <div className={classes.gradient} />
        <img src={props.image} alt="landscape" />
      </li>
    </Card>
  );
};

export default Items;
