import React, { Fragment, useState } from "react";
import Classes from "./Star.module.css";
import { useDispatch } from "react-redux";
import { dataActions } from "../../../store/data";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmark, faStar, faTrash, faSquarePlus } from "@fortawesome/free-solid-svg-icons";

const Star = (props) => {
  const [isHovered, setIsHovered] = useState(false);
  const dispatch = useDispatch();

  const handleClick = () => {
    dispatch(dataActions.handleDeleteChange())
  }


  const handleHover = () => {
    setIsHovered(true);
  };

  const handleLeave = () => {
    setIsHovered(false);
  };

  return (
    <Fragment>
      <div className={Classes.overlay} onMouseEnter={handleHover} onMouseLeave={handleLeave}>
        <div className={isHovered ? Classes.starHover : Classes.star}>
          <FontAwesomeIcon className={Classes.starIcon} icon={isHovered ? faXmark : faStar} />
        </div>
        {isHovered && 
        <div className={Classes.boxDelete}>
        <FontAwesomeIcon className={Classes.icon} icon={faTrash} onClick={handleClick}/>
        </div>
        }
        {isHovered && 
        <div className={Classes.boxAdd}>
        <FontAwesomeIcon className={Classes.icon} icon={faSquarePlus} onClick={handleClick}/>
        </div>
        }
        
      </div>
    </Fragment>
  );
};

export default Star;
