import React, { Fragment, useState } from "react";
import TrashClosedIcon from "../../../icons/trash-closed.svg";
import TrashOpenIcon from "../../../icons/trash-open.svg";
import { useDispatch, useSelector } from "react-redux";
import { dataActions } from "../../../store/data";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash } from "@fortawesome/free-solid-svg-icons";


const DeleteButton = () => {
  const isDelete = useSelector((state) => state.data.isDeleteEnabled);
  const dispatch = useDispatch();
  const [isHovered, setIsHovered] = useState(false);

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleLeave = () => {
    setIsHovered(false);
  };

  const handleClick = () => {
    dispatch(dataActions.handleDeleteChange())
  }



  return (
    <Fragment >
        <div onMouseEnter={handleHover} onMouseLeave={handleLeave} >
      {!isDelete && (
        <img 
        src={isHovered ? TrashOpenIcon : TrashClosedIcon}
        onClick={handleClick}
        alt="Enable Delete"
        />
      )}
      {isDelete && (
        <img
        src={isHovered ? TrashClosedIcon : TrashOpenIcon}
        onClick={handleClick}
        alt="Dissable Delete"
        />
      )}
      </div>
      <FontAwesomeIcon icon={faTrash} onClick={handleClick}/>
    </Fragment>
  );
};

export default DeleteButton;
