import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { dataActions } from "../../../store/data";
import Classes from "./AddPopup.module.css";

const AddPopup = () => {
  const isOpen = useSelector((state) => state.data.isAddEnabled);
  const dispatch = useDispatch();

  const handleClose = () => {
    dispatch(dataActions.handleAddChange());
  };

  if (!isOpen) {
    return null;
  }

  return (
    <div className={Classes.popupOverlay}>
      <div className={Classes.popupContent}>
        <button className={Classes.closeButton} onClick={handleClose}>
          Close
        </button>

        <form>
          <label htmlFor="title">Title:</label>
          <input type="text" id="title" name="title" maxLength="50" required />

          <label htmlFor="brand">Brand:</label>
          <input type="text" id="brand" name="brand" maxLength="50" required />

          <label htmlFor="category">Category:</label>
          <input type="text" id="category" name="category" maxLength="50" required />

          <label htmlFor="description" className={Classes.description}>Description:</label>
          <textarea id="description" name="description" maxLength="150" required></textarea>

          <label htmlFor="discountPercentage">Discount Percentage:</label>
          <input type="text" id="discountPercentage" name="discountPercentage" pattern="[0-9]+%" required />

          <label htmlFor="price">Price:</label>
          <input type="number" id="price" name="price" required />

          <label htmlFor="rating">Rating:</label>
          <input type="number" id="rating" name="rating" min="0" max="5" required />

          <label htmlFor="stock">Stock:</label>
          <input type="number" id="stock" name="stock" required />

          <label htmlFor="images">Images:</label>
          <input type="file" id="images" name="images" accept=".png, .jpg" multiple required />

            <div>
                <button type="submit">Submit</button> 
                <button onClick={handleClose}>Cancle</button>
            </div>
        </form>

      </div>
    </div>
  );
};

export default AddPopup;
