import React, { Fragment } from "react";
import { useSelector } from "react-redux";

const ViewDetails = (props) => {
  const dataId = useSelector((state) => state.data.viewDataId);
  const itemData = useSelector((state) =>
    state.data.storeData.find((item) => item.id === dataId)
  );

  if (!itemData) {
    return <p>Loading...</p>;
  }

  const {
    thumbnail,
    title,
    brand,
    category,
    price,
    discountPercentage,
    rating,
    stock,
  } = itemData;

  const images = [
    {
      original: thumbnail,
      thumbnail: thumbnail,
    },
  ];

  return (
    <Fragment>
      <div>
        <button>
          <p>back</p>
        </button>
      </div>

      <div>
        <img src={thumbnail} alt="Thumbnail" />

        <h2>Title: {title}</h2>

        <div>
          <h3>Brand: {brand}</h3>
          <h3>Category: {category}</h3>
        </div>

        <div>
          <h3>Price: {price}</h3>
          <h3>Discount Percentage: {discountPercentage}</h3>
        </div>

        <div>
          <h3>Rating: {rating}</h3>
          <h3>Stock: {stock}</h3>
        </div>

        <div>
          <img src={thumbnail} alt="Thumbnail" />
        </div>
      </div>
    </Fragment>
  );
};

export default ViewDetails;
