import { createSlice } from "@reduxjs/toolkit";

const initialState = { storeData: "", isDeleteEnabled: true };

const dataSlice = createSlice({
  name: "data",
  initialState: initialState,
  reducers: {
    dataInsert(state, action) {
      state.storeData = action.payload.products;


      console.log(state.storeData.products);
    },
    enableDelete(state) {
      state.isDeleteEnabled = true;


    },
    dissableDelete(state) {
      state.isDeleteEnabled = false;

    },
    deleteData(state, action) {
      const itemId = action.payload.itemId;
      state.storeData = state.storeData.filter((item) => item.id !== itemId);
    },
  },
});

export const dataActions = dataSlice.actions;

export default dataSlice.reducer;
