import React, { Fragment } from "react";
import { useSelector } from "react-redux";


const ViewDetails = (props) => {
    const isData = useSelector((state) => state.data.viewDataData);
    console.log(isData)
  const thumbnail = 'your-thumbnail-url';
  const title = 'Product Title';
  const brand = 'Product Brand';
  const category = 'Product Category';
  const price = 'Product Price';
  const discountPercentage = 'Product Discount Percentage'; // Corrected the variable name


  const rating = 'Product Rating';
  const stock = 'Product Stock';
  const images = [
    {
      original: thumbnail,
      thumbnail: thumbnail,
    },
  ];


  return (
    <Fragment>
      <div>
        <button>
          <p>back</p>
        </button>
      </div>


      <div>
        <img src={thumbnail} alt="Thumbnail" />


        <h2>Title: {title}</h2>


        <div>
          <h3>Brand: {brand}</h3>
          <h3>Category: {category}</h3>
        </div>


        <div>
          <h3>Price: {price}</h3>
          <h3>Discount Percentage: {discountPercentage}</h3> {/* Corrected the label */}
        </div>


        <div>
          <h3>Rating: {rating}</h3>
          <h3>Stock: {stock}</h3>
        </div>


        <div>
          <ImageGallery items={images} />
        </div>
      </div>
    </Fragment>
  );
};


export default ViewDetails;
