import React, { useState } from "react";
import Card from "../UI/Card";
import classes from "./Items.module.css";
import { deleteActions } from "../../store/delete";
import { useDispatch, useSelector } from 'react-redux';

const Items = (props) => {
  const [isHovered, setIsHovered] = useState(false);
  const isDelete = useSelector(state => state.delete.isDeleteEnabled)
  const dispatch = useDispatch();

  const handleHover = () => {
    setIsHovered(true);
  };

  const handleLeave = () => {
    setIsHovered(false);
  };

  const consoleClicked = () => {
    console.log(props.title);
  };

  const handleDelete = () => {
    dispatch(deleteActions.deleteItem(props.id)); // Dispatch the delete action with the item's ID
  };

  return (
    <Card>
      <li className={classes.list} onClick={consoleClicked}>
        <div
          onMouseEnter={handleHover}
          onMouseLeave={handleLeave}
          className={classes.overlay}
        >
          <div
            onMouseEnter={handleHover}
            onMouseLeave={handleLeave}
            className={classes.overlay}
          >
            <div className={classes.nameBox}>
              <h2 className={classes.title}>{props.title}</h2>
              <p className={classes.brand}>{props.brand}</p>
            </div>
            <div className={classes.priceBoxBorder}>
              <i className={classes.price}>$</i>
              <h3 className={classes.price}>{props.price}</h3>
            </div>
            {!isDelete && <div className={isHovered ? classes.gradientHover : classes.gradient} />}
            {isDelete && <div className={isHovered ? classes.gradientHoverDelete : classes.gradient} onClick={handleDelete} />}
            <img src={props.image} alt="landscape"></img>
          </div>
        </div>
      </li>
    </Card>
  );
};

export default Items;
